html:
{% set ns = namespace() %}

<!-- THIS IS A MASTER FILE, adapt the slave print formats accordingly -->

{% set ns.printSpecification = false %}

<style>
{% set site_url = frappe.utils.get_url() %}

@page {
    margin: 0cm; /* Set margin on each page */
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: normal;
	font-weight: 200;
	src: local('Univers LT Std 47 Cn Lt'), local('Univers LT Std 47 Cn Lt'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-LightCn.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: oblique;
	font-weight: 200;
	src: local('Univers LT Std 47 Cn Lt'), local('Univers LT Std 47 Cn Lt'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-LightCnObl.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: normal;
	font-weight: normal;
	src: local('Univers LT Std 57 Cn'), local('Univers LT Std 57 Cn'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-Cn.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: oblique;
	font-weight: normal;
	src: local('Univers LT Std 57 Cn'), local('Univers LT Std 57 Cn'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-CnObl.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: normal;
	font-weight: bold;
	src: local('Univers LT Std'), local('Univers LT Std'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-BoldCn.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: oblique;
	font-weight: bold;
	src: local('Univers LT Std'), local('Univers LT Std'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-BoldCnObl.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Pro';
	font-style: normal;
	font-weight: bold;
	src: local('Univers LT Pro'), local('Univers LT Pro'), url('{{ site_url }}/assets/erpnext/font/UniversLTPro-XBlack.otf') format('opentype');
}

</style>

{% if doc.shipping_address %}
{% set pay_addr = frappe.get_doc("Address", doc.customer_address) %}
{% endif %}

<!-- Do parsing -->

{% if doc.payment_terms_template is none %}
    {{ frappe.throw("ERROR: payment_terms_template not set") }}
{% endif %}

{% set ns.posCounter1=0 %}
{% set ns.posCounter2=0 %}
{% set ns.inSection=false %}
{% set ns.sectionPrices=[] %}
{% set ns.items=[] %}
{% set ns.sectionPrice=0 %}

{%- for item in doc.items -%}
    <!-- check discount on an item -->
    {% if item.discount_percentage > 0 %}
        {% set ns.discount=true %}
    {%- endif -%}

    <!-- do summation for sections -->
    {% if ns.inSection==true %}
        {% set ns.sectionPrice = ns.sectionPrice + item.amount %}
        {% set ns.posCounter2 = ns.posCounter2 + 1 %}
    {% else %}
        {% set ns.sectionPrice = item.amount %}
        {% set ns.posCounter1 = ns.posCounter1 + 1 %}
    {% endif %}

    {{ ns.items.append(item) or "" }}

    {% if item.item_group == "Format Elemente" %}
	    {% if item.amount != 0 %}
	        {{ frappe.throw("ERROR: Section Element (POS " + ns.posCounter1|string + ") must have a price of 0 EUR") }}
	    {% endif %}
    {% endif %}

    {% if item.item_group == "Format Elemente" and item.item_code == "Section Header" %}
	    {% set ns.posCounter2 = 0 %}
	    {% if ns.inSection == true %}
            {{ frappe.throw("ERROR: Nested Section Header at POS" + ns.posCounter1|string) }}
	    {% endif %}
	    {% set ns.inSection = true %}
    {% elif item.item_group == "Format Elemente" and item.item_code == "Section End" %}
	    {% if ns.inSection == false %}
            {{ frappe.throw("ERROR: Section END without Section Header at POS" + ns.posCounter1|string) }}
	    {% endif %}
	    {% set ns.inSection = false %}
         {{ ns.sectionPrices.append(ns.sectionPrice) or "" }}
    {% elif ns.inSection==false %}
        {{ ns.sectionPrices.append(item.amount) or "" }}
    {% endif %}
{%- endfor -%}

<!-- LETTER HEAD -->
{% if letter_head %}
<div class="print-format-header" id="header-html">
<table style="border: solid white 1px; margin-bottom: 3mm">
<tbody>
    <tr>
<td style="width: 80%; font-size: 9pt;">
<b>edevis GmbH</b> <br>
Handwerkstr 55, 70565 Stuttgart<br>
Tel. +49 711 933077-20 | Fax. +49 711 933077-99<br>
E-Mail: info@edevis.de, www.edevis.de<br>
<br>
</td>
<td style="width: 20%;">
<img alt="edevis logo" src="http://erp.e1.edevis.com/files/edevis_Logo_simple.png" style="width: 100%;">
</td>
</tr>
</tbody></table>
</div>
{% endif %}

<!-- ADDRESS LABEL -->
<table style="border: solid 1px white; font-size: 8pt;" >
<tr>
    <td style="width: 5mm; font-size: 9pt;">&nbsp;</td>
    <td style="width:60%; font-size: 10pt">
        <p style="font-size: 7pt; margin-bottom: 2em;">edevis GmbH | Handwerkstr. 55 | D-70565 Stuttgart</p>
    <strong>{{ doc.customer_name }}</strong><br/>
	{% if doc.shipping_address %}
	    {{ doc.shipping_address }}
	    <p>{{ frappe.db.get_value("Address", doc.shipping_address_name, "country") or "" }}</p>
	{% endif %}

	<br/>

	</td>
	<td style="width:25%">
	    <b>{{ _("Project") }}:</b><br>
	    <b>{{ _("Customer No.") }}:</b><br>
	    <b>{{ _("Customer's Purchase Order No") }}:</b><br>    
	    <b>{{ _("Processed by") }}:</b><br>
	    <b>{{ _("VAT-Id") }}:</b><br>    
	    <b>{{ _("Date") }}:</b><br>    
	</td>
	<td style="width: 15%; text-align: right;">
	    {{ doc.project or "" }}<br>
	    {{ frappe.db.get_value("Customer", doc.customer, "legacy_id") or "" }}<br>
	    {{ doc.po_no }}<br>
	    {{ frappe.get_fullname(doc.modified_by) }}<br>
	    {{ frappe.db.get_value("Company", doc.company, "tax_id") or "" }}<br>
	    {{ frappe.utils.formatdate(doc.posting_date, "dd.MM. yyyy") }}<br>
	</td>
</tr>    
</table>

<!-- HEADER -->
<div style="margin-bottom: 2em; margin-top: 5em;">
    <h5><span style="font-weight: normal;">{{ _(doc.doctype) }}</span><span style="font-weight: bold;"> {{ doc.name }}</span></h5>
</div>

<!-- PRICE TABLE -->
<table class=" table-sm pricetable">
	<thead>
		<tr>
			<th width="5%">{{ _("Pos") }}</th>
			<th width="10%" class="text-right">{{ _("Qty") }}</th>
			<th width="10%" class="text-right">{{ _("Weight") }}</th>
			<th width="75%">{{ _("Item") }}</th>
		</tr>
	</thead>
	<tbody>
	    {% set ns.posCounter1=0 %}
	    {% set ns.posCounter2=0 %}
	    {% set ns.inSection=false %}
        {% set ns.tableLine = "" %}

		{%- for item in ns.items -%}
		    {% if ns.inSection==true %}
	            {% set ns.posCounter2 = ns.posCounter2 + 1 %}
	        {% else %}
	            {% set ns.posCounter1 = ns.posCounter1 + 1 %}
	        {% endif %}

		    {% if item.item_group == "Format Elemente" and item.item_code == "Section Header" %}
        	    {% set ns.posCounter2 = 0 -%}
        	    {% set ns.inSection = true %}
    	    {% endif %}
		    {% if item.item_group == "Format Elemente" and item.item_code == "Section End" %}
        	    {% set ns.inSection = false %}
    	    {% else %}

    		<tr>
        	    {% if ns.inSection==true and item.item_group != "Format Elemente" %}
        	        {% set ns.tableLine = "" %}
    		        <td>{{ ns.posCounter1 }}.{{ ns.posCounter2 }}</td>
    	        {% else %}
    	            {% if item.item_group == "Format Elemente" %}
    	                {% set ns.tableLine = "tableLine sectionHeader" %}
    	            {% else %}
    	                {% set ns.tableLine = "tableLine" %}
    	            {% endif %}
    		        <td class="{{ ns.tableLine }}">{{ ns.posCounter1 }}</td>
    		    {% endif %}

                {%- if item.item_group == "Format Elemente" -%}     
    			    <td class="{{ ns.tableLine }}" colspan="3">{{ item.item_name }}</td>
    			{% else %}
    	    		{%- if (item.item_group == "Lohnleistungen") -%} 
            			<td class="{{ ns.tableLine }} text-right">1 LE</td>
        			{%- else -%}
            			<td class="{{ ns.tableLine }} text-right">{{ item.qty }} {{ item.uom or item.stock_uom }}</td>
        		    {%- endif -%}
        		    {%- if (item.total_weight > 0) -%} 
    			        <td class="{{ ns.tableLine }}">{{ item.total_weight }} {{ item.weight_uom }}</td>
    			    {% else %}
    			        <td class="{{ ns.tableLine }}">&nbsp;</td>
    			    {% endif %}
    			    <td class="{{ ns.tableLine }}">{{ item.item_code }}
            		{%- if item.item_name != item.item_code -%} 
            			 &nbsp; <i>{{ item.item_name }}</i>
            		{%- endif -%}

					{% if not doc.flags.print_settings.compact_item_print and item.item_group != "Format Elemente" %}
					    <p style="font-size: 8pt;">{{ _(item.description) }}</p>
        			{%- endif -%}

        			</td>
    			{% endif %}

<!--    			
                {%- if item.serial_no -%}
    				<br><b>{{ _("SR.No") }}:</b><br>
    				{{ item.serial_no | replace("\n", ", ") }}
    			{%- endif -%}
-->

    		</tr>


    		{% endif %}
		{%- endfor -%}
	</tbody>
</table>


{% if ns.printSpecification %}

<div class="page-break"></div>

<h3><span style="font-weight: normal;">{{ _("Specification for") }}</span> {{ doc.name }}</h3>
{%- for item in doc.items -%}
    {% if item.item_group != "Format Elemente" %}
    <div style="page-break-inside: avoid !important;">
    <!-- avoid page break after heading using a table -->
    <table style="width: 100%">
        <tr>
            <td style="width: 100%">
                <!-- heading with item code and item name -->
            	<h4 style="font-weight:normal;">{{ item.item_code }}  &nbsp;
            	{%- if item.item_name != item.item_code -%}
            		<b>{{ item.item_name }}</b>
            	{%- endif -%}
            	</h4>
                <!-- item picture -->

                {%- if not "img src" in _(item.description) and item.image  -%}
                <div style="max-width: 60%; margin-right: 10px;">
                    <img src="{{ item.image }}" style="max-width: 100%">
                </div>
                {%- endif -%}
            </td>
        </tr>
    </table>

    <p>{{ _(item.description) }}</p>

	<div style="height: 3em;">&nbsp;</div>
	</div>
	{% endif %}
{%- endfor -%}
{% endif %}

<p>&nbsp;</p>
{%- if doc.total_net_weight > 0 -%}
<p>Nettogewicht: {{ doc.total_net_weight }} kg</p>
{%- endif -%}

{%- if doc.incoterm != "" -%}
<p>Lieferbedingungen: {{ doc.incoterm }} {{ doc.named_place }}</p>
{%- endif -%}
<div id="footer-html" class="visible-pdf">
    <div class="print-format-footer">
        {{ footer }}
    </div>
</div>

