html:
{% set ns = namespace() %}
<!-- DO NOT EDIT THIS FILE -->
<!-- edit the  master file (QuoteExtended) instead, and set ns.printSpecification = false -->


{% if check_quoteitems(doc) %}
    {{ frappe.throw(check_quoteitems(doc)) }}
{% endif %}

<!-- Do printing -->

<style>
{% set site_url = frappe.utils.get_url() %}

@font-face {
	font-family: 'Univers LT Std';
	font-style: normal;
	font-weight: 200;
	src: local('Univers LT Std 47 Cn Lt'), local('Univers LT Std 47 Cn Lt'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-LightCn.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: oblique;
	font-weight: 200;
	src: local('Univers LT Std 47 Cn Lt'), local('Univers LT Std 47 Cn Lt'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-LightCnObl.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: normal;
	font-weight: normal;
	src: local('Univers LT Std 57 Cn'), local('Univers LT Std 57 Cn'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-Cn.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: oblique;
	font-weight: normal;
	src: local('Univers LT Std 57 Cn'), local('Univers LT Std 57 Cn'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-CnObl.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: normal;
	font-weight: bold;
	src: local('Univers LT Std'), local('Univers LT Std'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-BoldCn.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Std';
	font-style: oblique;
	font-weight: bold;
	src: local('Univers LT Std'), local('Univers LT Std'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-BoldCnObl.otf') format('opentype');
}

@font-face {
	font-family: 'Univers LT Pro';
	font-style: normal;
	font-weight: bold;
	src: local('Univers LT Pro'), local('Univers LT Pro'), url('{{ site_url }}/assets/erpnext/font/UniversLTPro-XBlack.otf') format('opentype');
}

</style>

<!-- LETTER HEAD -->
{% if letter_head %}
<div class="print-format-header" id="header-html">
    {{ letter_head }}
</div>
{% endif %}

<!-- ADDRESS LABEL -->
<table class="addressfield">
<tr>
    <td style="width:60%">
        <p class="addresssender">edevis GmbH | Handwerkstr. 55 | D-70565 Stuttgart</p>
    <strong>{{ doc.customer_name }}</strong><br/>
	{% if doc.address_display %}
	    {{ doc.address_display }}
	{% endif %}
	<br/>
	</td>
	<td style="width:40%">
		<table class="dense" style="width: 100%">
			<tr><td>{{ _("Customer No.") }}:</td><td style="text-align: right;">{{ frappe.db.get_value("Customer", doc.party_name, "legacy_id") or "" }}</td></tr>
			<tr><td>{{ _("Processed by") }}:</td><td style="text-align: right;">{{ frappe.get_fullname(doc.modified_by) or "" }}</td></tr>
			<tr><td>{{ _("Customer's Purchase Order") }}:</td><td style="text-align: right;">{{ doc.po_no or "" }}</td></tr>
			<tr><td>{{ _("Project") }}:</td><td style="text-align: right;">{{ doc.project or "" }}</td></tr>
			<tr><td>{{ _("Delivery Date") }}:</td><td style="text-align: right;">{{ frappe.utils.formatdate(doc.delivery_date, "dd.MM. yyyy") }}</td></tr>
			<tr><td>{{ _("Date") }}:</td><td style="text-align: right;">{{ frappe.utils.formatdate(doc.transaction_date, "dd.MM. yyyy") }}</td></tr>
		</table>
	</td>
</tr>    
</table>

<!-- HEADER -->
{% if doc.docstatus == 0 %}
<H2 style="text-align: center;">
    --- ENTWURF ---
</H2>
{% endif %}

{% set ns.Columns = 4 %}
{% if not doc.hide_item_price_within_section %}
	{% set ns.Columns = 5 %}
	{% if quoteitem_has_discount(doc) %}
        {% set ns.Columns = 6 %}
    {% endif %}
{% endif %}

<div style="margin-bottom: 2em;">
    <h5><span style="font-weight: normal;">{{ _("Invoice") }}</span><span style="font-weight: bold;"> {{ doc.name }}</span></h5>
</div>


<!-- PRICE TABLE -->
<table class=" table-sm pricetable">
	<thead>
		<tr>
			<th width="5%">{{ _("Pos") }}</th>
			<th width="30%">{{ _("Item") }}</th>
			<th width="10%" class="text-right">{{ _("Qty") }}</th>
			{% if not doc.hide_item_price_within_section %}
    			<th width="10%" class="text-right">{{ _("Rate") }}</th>
    			{% if quoteitem_has_discount(doc) %}
    			<th width="10%" class="text-right">{{ _("Discount") }}</th>
    			{% endif %}
			{% endif %}
			<th width="10%" class="text-right">{{ _("Amount") }}</th>
		</tr>
	</thead>
	<tbody>
	    <!-- add parent relationship info to the quotation_items => support hierarchical section headers -->
		{%- for item in structurize_quoteitem(doc) -%}
            <!-- format with separator lines if element is first order position (POS1), else without (POS 1.1) -->
            {% set ns.tableLine = "tableLine" if item.parent_item is none else "" %}
            <!-- format bold and with separator lines for section headers -->
            {% set ns.tableLine = "tableLine sectionHeader" if quoteitem_is_header(item) else ns.tableLine %}

    		<tr class="quoteitems {{ ns.tableLine }}">
		        <td class="">{{ item.position }}</td>

			    <td class="">{{ item.item_code if not quoteitem_is_header(item) else "" }}
        		{%- if item.item_name != item.item_code -%} 
        			 &nbsp; <i>{{ item.item_name }}</i>
		 			{% if not doc.flags.print_settings.compact_item_print and item.item_group != "Format Elemente" %}
                        <p>{{ _(item.description) }}</p>
                    {%- endif -%}
        		{%- endif -%}

    			</td>
    			{% if doc.flags.print_settings.print_uom_after_quantity %}
    			    <td class=" text-right">{{ item.qty if item.qty > 0 else "" }} {{ (item.uom or item.stock_uom) if item.qty > 0 else "" }}</td>
    			{% else %}
    			    <td class=" text-right">{{ (item.uom or item.stock_uom) if item.qty > 0 else "" }} {{ item.qty if item.qty > 0 else "" }}</td>
    			{% endif %}

				{% if not doc.hide_item_price_within_section %}
        		    <!-- conditional discount column -->
	        		{% if quoteitem_has_discount(doc) %}
    			        <td class=" text-right">{{ item.get_formatted("price_list_rate") if item.price_list_rate > 0 else item.get_formatted("rate") if item.rate > 0 else "" }}</td>
            			<td class="{{ ns.tableLine }} text-right">{{ item.get_formatted("discount_percentage") if item.discount_percentage > 0 else "" }}</td>
            		{% else %}
    			        <td class=" text-right">{{ item.get_formatted("rate") if item.rate > 0 else "" }}</td>
        			{% endif %}
    			{% endif %}


    		    <!-- amount column -->
    			<td class="{{ ns.tableLine }} text-right">{{ item.get_formatted("amount") if item.amount > 0 else "" }}</td>
    		</tr>

		{%- endfor -%}

		{% set ns.tableLine = "tableLine" %}

		<tr class="quoteitems {{ ns.tableLine }}">
			{% if doc.flags.show_inclusive_tax_in_print %}
				<td colspan="{{ ns.Columns-1 }}">
					{{ _("Total Excl. Tax") }}
				</td>
				<td class="text-right">
					{{ doc.get_formatted("net_total", doc) }}
				</td>
			{% else %}
				<td colspan="{{ ns.Columns-1 }}">
					{{ _("Total") }}
				</td>
				<td class="text-right">
					{{ doc.get_formatted("total", doc) }}
				</td>
			{% endif %}
		</tr>

		{%- if doc.discount_amount -%}
		<tr class="quoteitems {{ ns.tableLine }}">
			<td colspan="{{ ns.Columns-1 }}">
			    {%- if doc.additional_discount_percentage > 0 -%}
				    {{ _("Discount") }} ({{ doc.get_formatted("additional_discount_percentage") }} &percnt;)
				{% else %}
				    {{ _("Discount") }}
				{%- endif -%}    
			</td>
			<td class="text-right">
				{{ doc.get_formatted("discount_amount") }}
			</td>
		</tr>

		<tr class="quoteitems {{ ns.tableLine }}">
			<td colspan="{{ ns.Columns-1 }}">
			    {{ _("Net Total") }}
			</td>
			<td class="text-right">
				{{ doc.get_formatted("net_total") }}
			</td>
		</tr>
		{%- endif -%}		

		{%- for row in doc.taxes -%}
		  {%- if not row.included_in_print_rate or doc.flags.show_inclusive_tax_in_print -%}
		  {%- if row.tax_amount > 0 -%}
			<tr class="quoteitems {{ ns.tableLine }}">
				<td colspan="{{ ns.Columns-1 }}">
				    {% if '%' in row.description or row.rate == 0 %}
					    {{ row.description }}
					{% else %}
					    {{ row.description }}@{{ row.rate }}%
					{% endif %}
				</td>
				<td class="text-right">
					{{ row.get_formatted("tax_amount", doc) }}
				</td>
			<tr>
		    {% endif %}
		  {%- endif -%}
		{%- endfor -%}	

		<tr class="quoteitems {{ ns.tableLine }}">
			<td colspan="{{ ns.Columns-1 }}">
				<b>{{ _("Grand Total") }}</b>
			</td>
			<td class="text-right">
				<b>{{ doc.get_formatted("grand_total") }}</b>
			</td>
		</tr>
	</tbody>
</table>



<!-- FOOTER -->
<div id="footer-html" class="visible-pdf">
    <p id="pagenum">
        {{ _("Page {0} of {1}").format('<span class="page"></span>', '<span class="topage"></span>') }}
    </p>
    <div class="print-format-footer">
        {{ footer }}
    </div>
</div>

