}

tr.page-break {
  border-bottom: 1px solid black; /* FÃ¼gen Sie hier Ihre Linienstildefinition hinzu */
}

.pricetable td{
    border-left: 1px solid gray !important;
    border-right: 1px solid gray !important;
}

tr.tableLine {

    border-top: solid 1px gray;

}

tr:last-child.quoteitems {
    border-bottom: solid 2px gray;
}
.sectionHeader {
    font-weight: bold;
}

.spec td {
  border: 1px solid gray; 
}
.spec tr:first-child td {
  border-top: 0;
}
.spec tr td:first-child {
  border-left: 0;
}
.spec tr:last-child td {
  border-bottom: 0;
}
.spec tr td:last-child {
  border-right: 0;
}

html:
{% set ns = namespace() %}
<!-- DO NOT EDIT THIS FILE -->
<!-- edit the  master file (QuoteExtended) instead, and set ns.printSpecification = false -->

{% set ns.printSpecification = false %}

<!-- Do document checks -->
{% if not doc.payment_terms_template %}
    {{ quote_checkpaymentterms(doc) }}
{% endif %}


{% if check_quoteitems(doc) %}
    {{ frappe.throw(check_quoteitems(doc)) }}
{% endif %}

<!-- Do printing -->

<style>
{% set site_url = frappe.utils.get_url() %}

@font-face {
	font-family: 'Univers LT Std';
	font-style: normal;
	font-weight: 200;
	src: local('Univers LT Std 47 Cn Lt'), local('Univers LT Std 47 Cn Lt'), url('{{ site_url }}/assets/erpnext/font/UniversLTStd-LightCn.otf') format('opentype');
